insert into spell (name_spell, level_spell, component_v, component_s, component_m, duration_spell, distance_spell, time_of_application_spell, description_spell)
values ('Ведьмин снаряд','1 круг','В', 'C', 'М: веточка дерева, в которое ударила молния', '1 действие','30 футов','Концентрация, вплоть до 1 минуты','Луч потрескивающей синеватой энергии устремляется к существу в пределах дистанции, формируя между вами и целью непрерывный дуговой разряд. Совершите дальнобойную атаку заклинанием по этому существу. При попадании цель получает 1к12 урона электричеством, и пока заклинание активно, вы можете в каждый свой ход Действием автоматически причинять цели 1к12 урона электричеством. Это заклинание оканчивается, если вы Действием сделаете что-то иное. Заклинание также оканчивается, если цель окажется за пределами дистанции заклинания или получит от вас полное укрытие.
На более высоких уровнях: Если вы накладываете это заклинание, используя ячейку 2 уровня или выше, первичный урон увеличивается на 1к12 за каждый уровень ячейки выше первого.' );

insert into spell (name_spell, level_spell, component_v, component_s, component_m, duration_spell, distance_spell, time_of_application_spell, description_spell)
values ('Огненный шар','3 круг','В', 'C', 'М: крошечный шарик из гуано летучей мыши и серы', '1 действие','150 футов','Мгновенная','Яркий луч вылетает из вашего указательного пальца в точку, выбранную вами в пределах дистанции, где и происходит взрыв пламени с гулким рёвом. Все существа в пределах сферы с радиусом 20 футов с центром в этой точке должны совершить спасбросок Ловкости. Цель получает 8к6 урона огнём при провале или половину этого урона при успехе. Этот огонь огибает углы. Он воспламеняет горючие предметы, которые никто не несёт и не носит.
На более высоких уровнях: Если вы накладываете это заклинание, используя ячейку 4 уровня или выше, урон увеличивается на 1к6 за каждый уровень ячейки выше третьего.' );

insert into spell (name_spell, level_spell, component_v, component_s, component_m, duration_spell, distance_spell, time_of_application_spell, description_spell)
values ('Брызги кислоты','Заговор','В', 'C', ' ', '1 действие','60 футов','Мгновенная','Вы кидаете кислотный шарик. Выберите одно видимое вами существо в пределах дистанции или два видимых вами существа в пределах дистанции, находящихся в пределах 5 футов друг от друга. Цель должна преуспеть в спасброске Ловкости, иначе она получает 1к6 урона кислотой. Урон этого заклинания увеличивается на 1к6, когда вы достигаете 5 уровня (2к6), 11 уровня (3к6) и 17 уровня (4к6).' );

insert into spell (name_spell, level_spell, component_v, component_s, component_m, duration_spell, distance_spell, time_of_application_spell, description_spell)
values ('Вспышка мечей','Заговор','В', ' ', ' ', '1 действие','На себя (радиус 5 футов)','Мгновенная','Вы создаете круг призрачных клинков, которые кружатся вокруг вас. Все существа (исключая вас) в пределах 5 футов от вас должны преуспеть в спасброске Ловкости, иначе получат 1к6 урона силовым полем. Урон этого заклинания увеличивается на 1к6, когда вы достигаете 5-го уровня (2к6), 11-го уровня (3к6) и 17-го уровня (4к6).' );

insert into spell (name_spell, level_spell, component_v, component_s, component_m, duration_spell, distance_spell, time_of_application_spell, description_spell)
values ('Лассо молнии','Заговор','В', ' ', ' ', '1 действие','На себя (15 футовым радиусом)','Мгновенная','Вы создаёте хлыст из молний, поражающий одно существо по вашему выбору, которое вы можете видеть в пределах 15 футов от вас. Цель должна преуспеть в спасброске Силы, иначе будет притянута на 10 футов по прямой к вам, после чего получит 1к8 урона электричеством, если окажется в пределах 5 футов от вас. Урон этого заклинания увеличивается на 1к8, когда вы достигаете 5-го уровня (2к8), 11-го уровня (3к8) и 17-го уровня (4к8).' );

insert into spell (name_spell, level_spell, component_v, component_s, component_m, duration_spell, distance_spell, time_of_application_spell, description_spell)
values ('Клуб дыма','Заговор','В', 'С', ' ', '1 бонусное действие','30 футов','Мгновенная','Используя первородную силу огня, вы преобразуете близлежащий воздух в клубы дыма. Одно существо, которое вы можете видеть, должно совершить спасбросок Ловкости в случае провала она становится ослеплённым до начала вашего следующего хода. Клуб дыма не оказывает воздействия на существ с чувством вибрации или слепым зрением.' )

